import React, {useState} from 'react';
import {useRef} from 'react';
import Controls from './Controls';
import ReactPlayer from 'react-player';


function Video() {

  const thisVideo = useRef(null);
  const [isPlaying, updateIsPlaying] = useState(true);

  function handleClick() {
    console.log(thisVideo);
    console.log('chicken');
    if(!isPlaying) {
      thisVideo.play();
    } 
    updateIsPlaying(!isPlaying);
  }

  // var self = this;

  return <div className = "video-container">
  <video ref = {thisVideo}src = "http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4" 
     ></video>

  <Controls onPause = {handleClick()}/>
</div>;
}

export default Video;
