            <div className = 'progress-bar'>

                <div width = {props.percentageWatched} className = 'watched-bar'></div>

                <div className = 'playhead'></div>




            </div>
            <div className = 'time-remaining'>42:00</div>



    </div>;
}

export default ProgressControls;
